INSERT INTO `his_config_info` (`id`, `nid`, `data_id`, `group_id`, `app_name`, `content`, `md5`, `gmt_create`, `gmt_modified`, `src_user`, `src_ip`, `op_type`, `tenant_id`) VALUES (21, 45, 'share-config.yml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    druid:\n      app:\n        driverClassName: com.mysql.cj.jdbc.Driver\n        url: jdbc:mysql://43.138.76.94:3306/app?useUnicode=true&characterEncoding=utf8&&zeroDateTimeBehavior=convertToNull&useSSL=false&serverTimezone=GMT%2B8\n        username: root\n        password: root\n      sys:\n        driverClassName: com.mysql.cj.jdbc.Driver\n        enable: true\n        url: jdbc:mysql://43.138.76.94:3306/sys?useUnicode=true&characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=false&serverTimezone=GMT%2B8\n        username: root\n        password: root\n      #初始连接数\n      initialSize: 5\n      #最小连接池数量\n      minIdle: 10\n      #最大连接池数量\n      maxActive: 20\n      #配置获取连接等待超时的时间\n      maxWait: 60000\n      #配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\n      timeBetweenEvictionRunsMillis: 60000\n      #配置一个连接在池中最小生存的时间，单位是毫秒\n      minEvictableIdleTimeMillis: 300000\n      #配置一个连接在池中最大生存的时间，单位是毫秒\n      maxEvictableIdleTimeMillis: 900000\n      validationQuery: SELECT 1 FROM DUAL\n      testWhileIdle: true\n      testOnBorrow: false\n      testOnReturn: false\n      #打开PSCache，并且指定每个连接上PSCache的大小\n      poolPreparedStatements: true\n      maxPoolPreparedStatementPerConnectionSize: 20\n      #配置监控统计拦截的filters，去掉后监控界面sql无法统计，\'wall\'用于防火墙，此处是filter修改的地方\n      filters:\n        commons-log.connection-logger-name: stat,wall,log4j\n        #通过connectProperties属性来打开mergeSql功能；慢SQL记录\n        connectionProperties: druid.stat.mergeSql=true;druid.stat.slowSqlMillis=5000\n        #合并多个DruidDataSource的监控数据\n        useGlobalDataSourceStat: true\n      #配置 DruidStatFilter\n      web-stat-filter:\n        enabled: true\n        url-pattern: /*\n        exclusions: .js,*.gif,